import React, { useEffect, useRef, useState } from 'react';
import './Gallery.css';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';

const Gallery = () => {
    const images = [image1, image2, image3, image4, image5];
    const trackRef = useRef(null);
    const [progress, setProgress] = useState(0);
    const [focusedImage, setFocusedImage] = useState(null);
    const [exitingFocus, setExitingFocus] = useState(false);

    useEffect(() => {
        const track = trackRef.current;

        const handleOnDown = e => track.dataset.mouseDownAt = e.clientX;

        const handleOnUp = () => {
            track.dataset.mouseDownAt = "0";  
            track.dataset.prevPercentage = track.dataset.percentage;
        }

        const handleOnMove = e => {
            if(track.dataset.mouseDownAt === "0") return;

            const mouseDelta = parseFloat(track.dataset.mouseDownAt) - e.clientX,
                  maxDelta = window.innerWidth / 2;

            const percentage = (mouseDelta / maxDelta) * -100,
                  nextPercentageUnconstrained = parseFloat(track.dataset.prevPercentage) + percentage,
                  nextPercentage = Math.max(Math.min(nextPercentageUnconstrained, 0), -100);

            updateTrackPosition(nextPercentage);
        }

        const handleOnScroll = e => {
            if (focusedImage !== null) {
                e.preventDefault();
                exitFocusedImage();
                return;
            }
            e.preventDefault();
            const delta = Math.sign(e.deltaY) * 5;
            const nextPercentageUnconstrained = parseFloat(track.dataset.percentage || 0) - delta,
                  nextPercentage = Math.max(Math.min(nextPercentageUnconstrained, 0), -100);

            updateTrackPosition(nextPercentage);
        }

        const updateTrackPosition = (percentage) => {
            track.dataset.percentage = percentage;

            track.animate({
                transform: `translate(${percentage}%, -50%)`
            }, { duration: 1200, fill: "forwards" });

            for(const image of track.getElementsByClassName("image")) {
                image.animate({
                    objectPosition: `${100 + percentage}% center`
                }, { duration: 1200, fill: "forwards" });
            }

            setProgress(-percentage);
        }

        const preventScroll = e => {
            e.preventDefault();
            e.stopPropagation();
        }

        track.addEventListener('wheel', handleOnScroll, { passive: false });
        track.addEventListener('wheel', preventScroll, { passive: false });

        window.onmousedown = e => handleOnDown(e);
        window.ontouchstart = e => handleOnDown(e.touches[0]);
        window.onmouseup = e => handleOnUp(e);
        window.ontouchend = e => handleOnUp(e);
        window.onmousemove = e => handleOnMove(e);
        window.ontouchmove = e => handleOnMove(e.touches[0]);

        return () => {
            track.removeEventListener('wheel', handleOnScroll);
            track.removeEventListener('wheel', preventScroll);
            window.onmousedown = null;
            window.ontouchstart = null;
            window.onmouseup = null;
            window.ontouchend = null;
            window.onmousemove = null;
            window.ontouchmove = null;
        };
    }, [focusedImage]);

    const focusImage = (image) => {
        setFocusedImage(image);
    }

    const exitFocusedImage = () => {
        setExitingFocus(true);
        setTimeout(() => {
            setFocusedImage(null);
            setExitingFocus(false);
        }, 500);
    }

    return (
        <div className={`gallery-container ${focusedImage ? 'focused' : ''}`}>
            <div id="image-track" ref={trackRef} data-mouse-down-at="0" data-prev-percentage="0">
                {images.map((image, index) => (
                    <img 
                        key={index}
                        className={`image ${focusedImage === image ? 'focused' : ''} ${exitingFocus ? 'exiting' : ''}`}
                        src={image} 
                        alt={`Picture ${index + 1}`} 
                        draggable="false"
                        onClick={() => focusImage(image)}
                    />
                ))}
            </div>
            <div className="progress-bar">
                <div className="progress" style={{width: `${progress}%`}}></div>
            </div>
            {focusedImage && (
                <div className="focused-image-container" onClick={exitFocusedImage}>
                    <img 
                        src={focusedImage} 
                        alt="Focused" 
                        className={`focused-image ${exitingFocus ? 'exiting' : ''}`}
                    />
                </div>
            )}
        </div>
    );
};

export default Gallery;
