import React, { useState } from 'react';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import LoadingScreen from './components/LoadingScreen';
import SchoolInfo from './components/SchoolInfo';
import ClassRoutine from './components/ClassRoutine';
import SchoolLocation from './components/SchoolLocation';
import CustomCursor from './components/CustomCursor'; // Import Custom Cursor
import GrapesEditor from './components/GrapesEditor'; // Import GrapesJS Editor

const App = () => {
  const [showEditor, setShowEditor] = useState(false); // State to toggle GrapesJS editor

  return (
    <div className="App">
      <CustomCursor /> {/* Include Custom Cursor */}
      <LoadingScreen />
      <Navbar />
      <SchoolInfo />
      <ClassRoutine />
      <SchoolLocation />

      {/* Toggle Button for GrapesJS Editor */}
      <button 
        style={{
          position: 'fixed', top: '20px', right: '20px', padding: '10px 20px',
          fontSize: '16px', backgroundColor: '#007bff', color: 'white', border: 'none',
          borderRadius: '5px', cursor: 'pointer', zIndex: 10000
        }}
        onClick={() => setShowEditor(!showEditor)}>
        {showEditor ? 'Hide Editor' : 'Show Editor'}
      </button>

      {/* Conditionally Render the GrapesEditor */}
      {showEditor && <GrapesEditor />} 

      <Footer />
    </div>
  );
};

export default App;
