import React, { useEffect, useRef } from 'react';
import './Gallery.css';
import group from '../assets/images/group.jpg';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';
import image6 from '../assets/images/image6.jpg';
import image8 from '../assets/images/image8.jpg';

const Gallery = () => {
    const images = [group, image1, image2, image3, image4, image5, image6, image8];
    const captions = [
        "Class Photo 2024",
        "Donation to Disaster Funds",
        "International Mother Language Day, 2024",
        "World Environment Day, 2024",
        "Health Camp, 2024",
        "Picnic, 2024",
        "Picnic, 2023",
        "Sheikh Russel Day, 2022"
    ];
    const galleryRef = useRef(null);

    useEffect(() => {
        const gallery = galleryRef.current;

        const handleMouseMove = (e) => {
            const mouseX = e.clientX;
            const mouseY = e.clientY;

            const xDecimal = mouseX / window.innerWidth;
            const yDecimal = mouseY / window.innerHeight;

            const maxX = gallery.offsetWidth - window.innerWidth;
            const maxY = gallery.offsetHeight - window.innerHeight;

            const panX = maxX * xDecimal * -1;
            const panY = maxY * yDecimal * -1;

            gallery.animate({
                transform: `translate(${panX}px, ${panY}px)`
            }, {
                duration: 4000,
                fill: "forwards",
                easing: "ease"
            });
        };

        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    return (
        <div className="gallery-container">
            <h1 className="gallery-header">Grade IX Album</h1>
            <div id="gallery" ref={galleryRef}>
                {images.map((image, index) => (
                    <div key={index} className={`tile tile-${index + 1}`}>
                        <img src={image} alt={`Picture ${index + 1}`} />
                        <div className="image-caption">{captions[index]}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Gallery;
