import React, { useState, useEffect } from 'react';
import './Gallery.css';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';

const Gallery = () => {
    const images = [image1, image2, image3, image4, image5];
    const [currentIndex, setCurrentIndex] = useState(0);
    const animationClasses = ['from-left', 'from-right', 'from-bottom', 'from-top', 'zoom-in'];

    useEffect(() => {
        const timer = setTimeout(() => {
            document.querySelector('.picture-slot').classList.add('active');
        }, 100);
        return () => clearTimeout(timer);
    }, [currentIndex]);

    const nextSlide = () => {
        document.querySelector('.picture-slot').classList.remove('active');
        setTimeout(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 300);
    };

    const prevSlide = () => {
        document.querySelector('.picture-slot').classList.remove('active');
        setTimeout(() => {
            setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
        }, 300);
    };

    return (
        <div className="gallery-container">
            <div className="gallery">
                <div className={`picture-slot ${animationClasses[currentIndex % animationClasses.length]}`}>
                    <img src={images[currentIndex]} alt={`Picture ${currentIndex + 1}`} />
                </div>
                <button className="nav-button prev" onClick={prevSlide}>&lt;</button>
                <button className="nav-button next" onClick={nextSlide}>&gt;</button>
            </div>
            <div className="indicators">
                {images.map((_, index) => (
                    <span 
                        key={index} 
                        className={`indicator ${index === currentIndex ? 'active' : ''}`}
                        onClick={() => setCurrentIndex(index)}
                    ></span>
                ))}
            </div>
        </div>
    );
};

export default Gallery;
