import React, { useEffect, useRef } from 'react';
import './Gallery.css';

// Import your images
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';
import image6 from '../assets/images/image6.jpg';
import image8 from '../assets/images/image8.jpg';
import group from '../assets/images/group.jpg';

/*************  ✨ Codeium Command ⭐  *************/
/**
 * A responsive, interactive gallery component.
 * 
 * It uses the CSS grid layout to position the images in a responsive grid.
 * The images are also animated to pan when the user moves the mouse.
 * 
 * @returns {JSX.Element} The gallery component.
 */
/******  95b1a6ad-4774-48d1-95a5-c454646570e8  *******/
const Gallery = () => {
    const galleryRef = useRef(null);
    const images = [group, image1, image2, image3, image4, image5, image6, image8, group];
    const captions = [
        "Class Photo 2024",
        "Donation to Disaster Funds",
        "International Mother Language Day, 2024",
        "World Environment Day, 2024",
        "Health Camp, 2024",
        "Picnic, 2024",
        "Picnic, 2023",
        "Sheikh Russel Day, 2022",
        "Class Photo 2023"
    ];

    useEffect(() => {
        const gallery = galleryRef.current;

        const handleMouseMove = (e) => {
            const mouseX = e.clientX,
                  mouseY = e.clientY;

            const xDecimal = mouseX / window.innerWidth,
                  yDecimal = mouseY / window.innerHeight;

            const maxX = gallery.offsetWidth - window.innerWidth,
                  maxY = gallery.offsetHeight - window.innerHeight;

            const panX = maxX * xDecimal * -1,
                  panY = maxY * yDecimal * -1;

            gallery.animate({
                transform: `translate(${panX}px, ${panY}px)`
            }, {
                duration: 4000,
                fill: "forwards",
                easing: "ease"
            });
        };

        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    return (
        <div className="gallery-wrapper">
            <div className="gallery-overlay">Hover over the boxes</div>
            <div id="gallery" ref={galleryRef}>
                {images.map((image, index) => (
                    <div key={index} className={`tile tile-${index + 1}`}>
                        <img src={image} alt={`Tile ${index + 1}`} />
                        <div className="caption">{captions[index]}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Gallery;
