import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Buttons.css';

const Card = ({ title, subtitle, icon, onClick }) => {
  const subtitleRef = useRef(null);

  useEffect(() => {
    const subtitleElement = subtitleRef.current;
    if (!subtitleElement) return;

    const createWord = (text, index) => {
      const word = document.createElement("span");
      word.innerHTML = `${text} `;
      word.classList.add("card-subtitle-word");
      word.style.transitionDelay = `${index * 40}ms`;
      return word;
    }

    const addWord = (text, index) => subtitleElement.appendChild(createWord(text, index));
    const createSubtitle = text => text.split(" ").map(addWord);

    subtitleElement.innerHTML = '';
    createSubtitle(subtitle);
  }, [subtitle]);

  return (
    <div className="card" onClick={onClick}>
      <div className="card-content">
        <h3 className="card-title">{title}</h3>
        <h4 className="card-subtitle" ref={subtitleRef}></h4>
      </div>
      <i className={`card-icon fa-solid ${icon}`}></i>
    </div>
  );
}

const Buttons = () => {
  const navigate = useNavigate();

  const cards = [
    { title: "Notice Board", subtitle: "View latest notices", icon: "fa-book", path: "/notice" },
    { title: "Class Schedule", subtitle: "View class schedule", icon: "fa-calendar", path: "/routine" },
    { title: "G-IX Gallery", subtitle: "View Grade IX photos", icon: "fa-image", path: "/gallery" },
    { title: "G-IX Chatroom", subtitle: "Access G-IX chatroom", icon: "fa-comment", path: "/chatroom" },
    { title: "Project Info", subtitle: "About the project", icon: "fa-users", path: "/about" },
  ];

  const handleCardClick = (path) => {
    navigate(path);
  };

  return (
    <div className="buttons-page">
      <div className="buttons-container">
        {cards.map((card, index) => (
          <Card 
            key={index} 
            title={card.title} 
            subtitle={card.subtitle} 
            icon={card.icon} 
            onClick={() => handleCardClick(card.path)}
          />
        ))}
      </div>
    </div>
  );
}

export default Buttons;
