import React, { useEffect, useRef } from 'react';
import './SchoolInfo.css';

const SchoolInfo = () => {
  const subtitleRef = useRef(null);

  useEffect(() => {
    const subtitleElement = subtitleRef.current;
    if (!subtitleElement) return;

    const createWord = (text, index) => {
      const word = document.createElement("span");
      word.innerHTML = `${text} `;
      word.classList.add("card-subtitle-word");
      word.style.transitionDelay = `${index * 40}ms`;
      return word;
    }

    const addWord = (text, index) => subtitleElement.appendChild(createWord(text, index));
    const createSubtitle = text => text.split(" ").map(addWord);

    subtitleElement.innerHTML = '';
    createSubtitle("The primary purpose of this website is to establish communication and information sharing between peers of JISC Grade IX.");
  }, []);

  return (
    <div className="about-page">
      <div className="about-container">
        <div className="cards-row">
          <div className="card school-info-card">
            <div className="card-content">
              <h3 className="card-title">Jahanara Israil School & College</h3>
              <h4 className="card-subtitle" ref={subtitleRef}></h4>
            </div>
            <i className="card-icon fa-solid fa-school"></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SchoolInfo;
