import React from 'react';
import { useNode } from '@craftjs/core';

// This version is used within the Editor
export const Button = ({ text }) => {
  const { connectors: { connect, drag } } = useNode();
  return (
    <button ref={ref => connect(drag(ref))} style={{ margin: '5px', padding: '10px' }}>
      {text}
    </button>
  );
};

// This version is used outside the Editor
export const ToolboxButton = ({ text, onClick }) => {
  return (
    <button onClick={onClick} style={{ margin: '5px', padding: '10px' }}>
      {text}
    </button>
  );
};
