import React, { useEffect, useState } from 'react';
import '../CustomCursor.css'; // Your CSS file for styling the cursor

const CustomCursor = () => {
    const [position, setPosition] = useState({ x: 0, y: 0 });

    useEffect(() => {
        const updatePosition = (e) => {
            const cursorSize = 20; // Adjust based on your cursor size
            const x = e.clientX;
            const y = e.clientY;

            const windowWidth = window.innerWidth;
            const windowHeight = window.innerHeight;

            // Calculate the bounds so the cursor doesn't go off the screen
            const newX = Math.min(windowWidth - cursorSize, Math.max(0, x));
            const newY = Math.min(windowHeight - cursorSize, Math.max(0, y));

            setPosition({ x: newX, y: newY });
        };

        // Event listeners for mouse movement and window resizing
        window.addEventListener('mousemove', updatePosition);
        window.addEventListener('scroll', updatePosition);
        window.addEventListener('resize', updatePosition);

        return () => {
            // Clean up event listeners
            window.removeEventListener('mousemove', updatePosition);
            window.removeEventListener('scroll', updatePosition);
            window.removeEventListener('resize', updatePosition);
        };
    }, []);

    return (
        <div
            className="custom-cursor"
            style={{
                left: `${position.x}px`,
                top: `${position.y}px`,
                transform: `translate(-50%, -50%)`, // Center the cursor
            }}
        />
    );
};

export default CustomCursor;
