import React from 'react';
import { Editor } from 'react-block-editor';

const WebEditor = () => {
  const [editorState, setEditorState] = React.useState(null);

  React.useEffect(() => {
    const initEditor = async () => {
      const editorInstance = await Editor.initEditor({
        container: 'editor-container',
        // TODO: configure editor with templates, images, and React/HTML code generation
      });
      setEditorState(editorInstance);
    };
    initEditor();
  }, []);

  return (
    <div id="editor-container" style={{ width: '100%', height: '500px' }} />
  );
};

export default WebEditor;
