import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './SchoolLocation.css';

const SchoolLocation = () => {
    useEffect(() => {
        // Initialize the map with dragging and zoom control disabled
        const map = L.map('mapContainer', {
            dragging: false,
            zoomControl: false,
            scrollWheelZoom: false,
            doubleClickZoom: false,
            touchZoom: false,
            boxZoom: false,
            keyboard: false
        }).setView([22.708389, 90.359921], 15);

        // Add OpenStreetMap tile layer
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        }).addTo(map);

        // Create a default icon
        const defaultIcon = L.icon({
            iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
            iconSize: [25, 41],
            iconAnchor: [12, 41],
            popupAnchor: [1, -34],
            shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
            shadowSize: [41, 41]
        });

        // Add a marker at the center
        L.marker([22.708389, 90.359921], { icon: defaultIcon })
            .addTo(map)
            .bindPopup('Jahanara Israil School & College, 999 College Row, Barishal 8200');

        // Remove the custom control button
        // (since it's not needed for a non-interactive map)

    }, []);

    return <div id="mapContainer" style={{ height: '400px', width: '100%' }} />;
};

export default SchoolLocation;
