import React, { useEffect } from 'react';
import './About.css';

const Card = ({ title, subtitle }) => {
  useEffect(() => {
    const subtitleElement = document.querySelector('.card-subtitle');
    const createWord = (text, index) => {
      const word = document.createElement("span");
      word.innerHTML = `${text} `;
      word.classList.add("card-subtitle-word");
      word.style.transitionDelay = `${index * 40}ms`;
      return word;
    }
    const addWord = (text, index) => subtitleElement.appendChild(createWord(text, index));
    const createSubtitle = text => text.split(" ").map(addWord);
    createSubtitle(subtitle);
  }, [subtitle]);

  return (
    <div className="card">
      <div className="card-content">
        <h3 className="card-title">{title}</h3>
        <h4 className="card-subtitle"></h4>
      </div>
      <i className="card-icon fa-solid fa-code"></i>
    </div>
  );
}

const About = () => {
  const cards = [
    { title: "Sadat Sahib", subtitle: "But in a much more real sense, I have no idea what I'm doing." },
    { title: "Sadat Sahib", subtitle: "But in a much more real sense, I have no idea what I'm doing." },
    { title: "Sadat Sahib", subtitle: "But in a much more real sense, I have no idea what I'm doing." },
    { title: "Sadat Sahib", subtitle: "But in a much more real sense, I have no idea what I'm doing." },
    { title: "Sadat Sahib", subtitle: "But in a much more real sense, I have no idea what I'm doing." },
  ];

  return (
    <div className="about-container">
      <div className="cards-row">
        {cards.slice(0, 3).map((card, index) => (
          <Card key={index} title={card.title} subtitle={card.subtitle} />
        ))}
      </div>
      <div className="cards-row">
        {cards.slice(3, 5).map((card, index) => (
          <Card key={index + 3} title={card.title} subtitle={card.subtitle} />
        ))}
      </div>
    </div>
  );
}

export default About;
