import React, { useEffect } from 'react';

const SchoolLocation = () => {
    useEffect(() => {
        const platform = new H.service.Platform({
            apikey: 'q4gwOr4LRf76boaHs_TrqPnsu5ZIAg1_9qx0vQbftrA'
        });

        const defaultLayers = platform.createDefaultLayers();

        // Initialize the map
        const map = new H.Map(
            document.getElementById('mapContainer'),
            defaultLayers.vector.normal.map,
            {
                center: { lat: 22.708389, lng: 90.359921 },
                zoom: 15,
                pixelRatio: window.devicePixelRatio || 1
            }
        );

        // Enable the event system and the default behavior for map interactions
        const behavior = new H.mapevents.Behavior(new H.mapevents.MapEvents(map));

        // Create the default UI components
        const ui = H.ui.UI.createDefault(map, defaultLayers);

        // Add a marker at the center
        const marker = new H.map.Marker({ lat: 22.708389, lng: 90.359921 });
        map.addObject(marker);
    }, []);

    return <div id="mapContainer" style={{ height: '400px', width: '100%' }} />;
};

export default SchoolLocation;
