import React, { useEffect, useState, useRef } from 'react';
import './SchoolInfo.css';

const SchoolInfo = () => {
    const [visible, setVisible] = useState(false);
    const schoolInfoRef = useRef(null);

    useEffect(() => {
        setVisible(true);
    }, []);

    const handleMouseMove = (e) => {
        if (schoolInfoRef.current) {
            const rect = schoolInfoRef.current.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            schoolInfoRef.current.style.setProperty('--mouse-x', `${x}px`);
            schoolInfoRef.current.style.setProperty('--mouse-y', `${y}px`);
        }
    };

    return (
        <div 
            className={`school-info ${visible ? 'visible' : ''}`}
            ref={schoolInfoRef}
            onMouseMove={handleMouseMove}
        >
            <h2>Jahanara Israil School & College</h2>
            <p>The primary purpose of this website is to establish communication and information sharing between peers of JISC Grade IX.</p>
        </div>
    );
};

export default SchoolInfo;
