import React, { useState, useEffect } from 'react';
import './GroupCall.css';
import io from 'socket.io-client';

const socket = io('https://6711c250-f6ca-4f9f-a6e2-2b3df23ce26f-00-3paa249pb63vs.sisko.replit.dev:443');

const GroupCall = ({ user }) => {
  const [isCallActive, setIsCallActive] = useState(false);
  const [participants, setParticipants] = useState([]);

  useEffect(() => {
    socket.on('callParticipantsUpdate', (updatedParticipants) => {
      setParticipants(updatedParticipants);
    });

    return () => {
      socket.off('callParticipantsUpdate');
    };
  }, []);

  const toggleCall = () => {
    if (isCallActive) {
      leaveCall();
    } else {
      joinCall();
    }
    setIsCallActive(!isCallActive);
  };

  const joinCall = () => {
    const participant = { id: user.id, name: user.name };
    socket.emit('joinCall', participant);
  };

  const leaveCall = () => {
    socket.emit('leaveCall', user.id);
  };

  return (
    <div className="group-call-container">
      <button onClick={toggleCall} className="call-icon">
        {isCallActive ? 'ðŸ“ž' : 'ðŸ“ž'}
      </button>
      {isCallActive && (
        <div className="call-panel">
          <h3>Group Call</h3>
          <ul className="participants-list">
            {participants.map((participant) => (
              <li key={participant.id} className="participant-item">
                <span className="participant-name">{participant.name}</span>
                <span className="participant-status">ðŸŸ¢</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default GroupCall;
