import React, { useEffect, useRef } from 'react';
import './Gallery.css';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';

const Gallery = () => {
    const images = [image1, image2, image3, image4, image5];
    const trackRef = useRef(null);

    useEffect(() => {
        const track = trackRef.current;

        const handleOnDown = e => track.dataset.mouseDownAt = e.clientX;

        const handleOnUp = () => {
            track.dataset.mouseDownAt = "0";  
            track.dataset.prevPercentage = track.dataset.percentage;
        }

        const handleOnMove = e => {
            if(track.dataset.mouseDownAt === "0") return;

            const mouseDelta = parseFloat(track.dataset.mouseDownAt) - e.clientX,
                  maxDelta = window.innerWidth / 2;

            const percentage = (mouseDelta / maxDelta) * -100,
                  nextPercentageUnconstrained = parseFloat(track.dataset.prevPercentage) + percentage,
                  nextPercentage = Math.max(Math.min(nextPercentageUnconstrained, 0), -100);

            // Slow down the movement by applying a factor
            const slowDownFactor = 0.1;
            const smoothNextPercentage = parseFloat(track.dataset.percentage) + ((nextPercentage - parseFloat(track.dataset.percentage)) * slowDownFactor);

            track.dataset.percentage = smoothNextPercentage;

            track.style.transform = `translate(${smoothNextPercentage}%, -50%)`;

            for(const image of track.getElementsByClassName("image")) {
                image.style.objectPosition = `${100 + smoothNextPercentage}% center`;
            }
        }

        track.onmousedown = e => handleOnDown(e);
        track.ontouchstart = e => handleOnDown(e.touches[0]);
        document.onmouseup = e => handleOnUp(e);
        document.ontouchend = e => handleOnUp(e.touches[0]);
        document.onmousemove = e => handleOnMove(e);
        document.ontouchmove = e => handleOnMove(e.touches[0]);

        return () => {
            track.onmousedown = null;
            track.ontouchstart = null;
            document.onmouseup = null;
            document.ontouchend = null;
            document.onmousemove = null;
            document.ontouchmove = null;
        };
    }, []);

    return (
        <main className="gallery-page">
            <div className="gallery-container">
                <div id="image-track" ref={trackRef} data-mouse-down-at="0" data-prev-percentage="0">
                    {images.map((image, index) => (
                        <img 
                            key={index}
                            className="image" 
                            src={image} 
                            alt={`Picture ${index + 1}`} 
                            draggable="false"
                        />
                    ))}
                </div>
            </div>
        </main>
    );
};

export default Gallery;
