import React, { useState, useEffect } from 'react';
import './Chatroom.css';

const Chatroom = ({ user }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [image, setImage] = useState(null);

  useEffect(() => {
    // Load messages from backend
    fetch('http://localhost:3001/messages')
      .then((response) => response.json())
      .then((data) => setMessages(data.messages));
  }, []);

  const handleSendMessage = () => {
    if (input.trim() !== '' || image) {
      const formData = new FormData();
      formData.append('text', input);
      formData.append('user', user.name);
      formData.append('userId', user.id);
      formData.append('timestamp', new Date().toISOString());
      if (image) {
        formData.append('image', image);
      }

      fetch('http://localhost:3001/messages', {
        method: 'POST',
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          setMessages([...messages, { id: data.id, text: input, user: user.name, userId: user.id, timestamp: new Date().toISOString(), imagePath: image ? URL.createObjectURL(image) : null }]);
          setInput('');
          setImage(null);
        });
    }
  };

  const handleDeleteMessage = (messageId) => {
    if (user.admin || messages.find((msg) => msg.id === messageId).userId === user.id) {
      fetch(`http://localhost:3001/messages/${messageId}`, {
        method: 'DELETE',
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.deleted) {
            setMessages(messages.filter((message) => message.id !== messageId));
          } else {
            alert('Failed to delete the message.');
          }
        });
    } else {
      alert('You do not have permission to delete this message.');
    }
  };

  const handleEditMessage = (messageId, newText) => {
    const now = new Date();
    const message = messages.find((msg) => msg.id === messageId);
    const messageTime = new Date(message.timestamp);
    const timeDiff = (now - messageTime) / (1000 * 60); // time difference in minutes

    if (timeDiff <= 5) {
      setMessages(
        messages.map((msg) =>
          msg.id === messageId ? { ...msg, text: newText } : msg
        )
      );
    } else {
      alert('You can only edit messages up to 5 minutes old.');
    }
  };

  return (
    <div className="chatroom">
      <h2>Grade-IX Chat Room</h2>
      <div className="chatroom-messages">
        {messages.length === 0 ? (
          <div className="chatroom-welcome">Send a message to get started</div>
        ) : (
          messages.map((message) => (
            <div key={message.id} className="chatroom-message">
              <span>{message.user}: {message.text}</span>
              {message.imagePath && <img src={`http://localhost:3001/${message.imagePath}`} alt="Uploaded" />}
              {(user.admin || message.userId === user.id) && (
                <div className="chatroom-message-actions">
                  <button onClick={() => handleDeleteMessage(message.id)}>Delete</button>
                  <button onClick={() => {
                    const newText = prompt('Edit your message:', message.text);
                    if (newText !== null) {
                      handleEditMessage(message.id, newText);
                    }
                  }}>Edit</button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
      <div className="chatroom-input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="chatroom-input"
          placeholder="Type a message..."
        />
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="chatroom-input"
        />
        <button onClick={handleSendMessage} className="chatroom-send-button">Send</button>
      </div>
    </div>
  );
};

export default Chatroom;
