import React, { useState } from 'react';
import { Editor, Frame, Element } from '@craftjs/core';
import { Text } from './editor/Text';
import { Container } from './editor/Container';
import { Card } from './editor/Card';
import { Button, ToolboxButton } from './editor/Button';
import { Image } from './editor/Image';

export const WebEditor = () => {
  const [json, setJson] = useState(null);

  const addElement = (type) => {
    // This function will be implemented to add elements to the editor
    console.log(`Adding ${type}`);
  };

  return (
    <div id="editor">
      <h2>Web Editor</h2>
      <Editor
        resolver={{ Card, Text, Container, Button, Image }}
        onNodesChange={(query) => {
          const json = query.serialize();
          setJson(json);
        }}
      >
        <Frame>
          <Element is={Container} padding={5} background="#eee" canvas>
            <Card />
            <Text text="Edit this text" />
            <Button text="Click me" />
            <Image src="https://via.placeholder.com/150" alt="Placeholder" />
          </Element>
        </Frame>
      </Editor>
      <div style={{ marginTop: '2rem' }}>
        <h3>Toolbox</h3>
        <ToolboxButton text="Add Text" onClick={() => addElement('text')} />
        <ToolboxButton text="Add Card" onClick={() => addElement('card')} />
        <ToolboxButton text="Add Image" onClick={() => addElement('image')} />
      </div>
      <div style={{ marginTop: '2rem' }}>
        <h3>Editor JSON</h3>
        <pre>{JSON.stringify(json, null, 2)}</pre>
      </div>
    </div>
  );
};
