import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const SchoolLocation = () => {
    useEffect(() => {
        // Initialize the map
        const map = L.map('mapContainer').setView([22.708389, 90.359921], 15);

        // Add OpenStreetMap tile layer
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        }).addTo(map);

        // Create a default icon
        const defaultIcon = L.icon({
            iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
            iconSize: [25, 41],
            iconAnchor: [12, 41],
            popupAnchor: [1, -34],
            shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
            shadowSize: [41, 41]
        });

        // Optional: Add a marker at the center
        const marker = L.marker([22.708389, 90.359921], { icon: defaultIcon })
            .addTo(map)
            .bindPopup('School Location')
            .openPopup();
    }, []);

    return <div id="mapContainer" style={{ height: '400px', width: '100%' }} />;
};

export default SchoolLocation;
