import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

const GrapesEditor = () => {
    useEffect(() => {
        const editor = grapesjs.init({
            container: '#gjs',
            fromElement: true,
            height: '100%',
            width: 'auto',
            storageManager: false,
            panels: { defaults: [] },
            blockManager: {
                appendTo: '#blocks',
                blocks: [
                    {
                        id: 'react-header',
                        label: '<b>Header</b>',
                        content: `
                        <div>
                            <h1 className="header-title">Welcome to My Website</h1>
                        </div>`,
                    },
                    {
                        id: 'react-button',
                        label: '<b>Button</b>',
                        content: `
                        <button className="btn-primary">Click Me</button>`,
                    },
                    {
                        id: 'react-section',
                        label: 'React Section',
                        content: `
                        <div className="section">
                            <h2 className="section-title">Section Title</h2>
                            <p>This is a section of content that uses JSX.</p>
                        </div>`,
                    },
                ],
            },
        });

        // Adding an export button to the editor panel
        editor.Panels.addButton('options', {
            id: 'export-react',
            className: 'btn-export',
            label: 'Export React Code',
            command: 'export-react',
        });

        // Command to export the React JSX code
        editor.Commands.add('export-react', {
            run(editor, sender) {
                sender && sender.set('active', 0); // Deactivate the button

                const code = editor.getHtml();  // Get the HTML content from the editor
                const css = editor.getCss();    // Get the CSS content if needed

                const transformedCode = htmlToJSX(code); // Convert HTML to JSX

                const completeCode = `
                import React from 'react';

                const MyComponent = () => (
                  <>
                  ${transformedCode}
                  </>
                );

                export default MyComponent;
                `;

                downloadJSX('MyComponent.jsx', completeCode); // Save the JSX code as a .jsx file
            },
        });
    }, []);

    return (
        <div style={{ display: 'flex' }}>
            <div id="blocks" style={{ width: '300px', padding: '10px', background: '#f4f4f4' }}></div>
            <div id="gjs" style={{ height: '100vh', width: '100%' }}></div>
        </div>
    );
};

// Helper function to transform HTML to JSX
function htmlToJSX(html) {
    return html
        .replace(/class=/g, 'className=') // Convert "class" to "className" for JSX
        .replace(/for=/g, 'htmlFor=');    // Convert "for" to "htmlFor" for JSX
}

// Helper function to download JSX code as a file
function downloadJSX(filename, content) {
    const element = document.createElement('a');
    const file = new Blob([content], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = filename;
    document.body.appendChild(element);
    element.click();
}

export default GrapesEditor;
