import React from 'react';
import HereMapsApi from '../utils/hereMapsApi';

const LocationMap = () => {
  const [map, setMap] = React.useState(null);
  const [marker, setMarker] = React.useState(null);

  React.useEffect(() => {
    const initMap = async () => {
      const mapInstance = await HereMapsApi.initMap({
        container: 'map-container',
        center: [22.70838910569462, 90.3599216846542],
        zoom: 15,
      });
      setMap(mapInstance);

      const markerInstance = await HereMapsApi.addMarker({
        map: mapInstance,
        coordinates: [22.70838910569462, 90.3599216846542],
      });
      setMarker(markerInstance);
    };
    initMap();
  }, []);

  return (
    <div id="map-container" style={{ width: '100%', height: '300px' }} />
  );
};

export default LocationMap;
