import React, { useEffect, useRef } from 'react';
import './Gallery.css';
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';

const Gallery = () => {
    const images = [image1, image2, image3, image4, image5];
    const observer = useRef();
    const galleryRef = useRef();

    const animationClasses = ['from-left', 'from-right', 'from-bottom', 'from-top', 'zoom-in'];

    useEffect(() => {
        observer.current = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('visible');
                } else {
                    entry.target.classList.remove('visible');
                }
            });
        }, { threshold: 0.5 });

        const elements = document.querySelectorAll('.picture-slot');
        elements.forEach(element => observer.current.observe(element));

        return () => {
            if (observer.current) {
                observer.current.disconnect();
            }
        };
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.pageYOffset;
            const windowHeight = window.innerHeight;
            const elements = document.querySelectorAll('.picture-slot');

            elements.forEach((element, index) => {
                const rect = element.getBoundingClientRect();
                const elementTop = rect.top + scrollPosition;
                const elementVisible = 150;

                if (elementTop < scrollPosition + windowHeight - elementVisible) {
                    element.classList.add('active');
                } else {
                    element.classList.remove('active');
                }
            });
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className="gallery" ref={galleryRef}>
            {images.map((image, index) => (
                <div className={`picture-slot ${animationClasses[index % animationClasses.length]}`} key={index}>
                    <img src={image} alt={`Picture ${index + 1}`} />
                </div>
            ))}
        </div>
    );
};

export default Gallery;
