import React, { useEffect, useState } from 'react';
import '../CustomCursor.css';

const CustomCursor = () => {
    const [position, setPosition] = useState({ x: 0, y: 0 });
    const [isClicking, setIsClicking] = useState(false);
    const [isVisible, setIsVisible] = useState(false);
    const [isPointer, setIsPointer] = useState(false);

    useEffect(() => {
        const isTouchDevice = ('ontouchstart' in window) || (navigator.maxTouchPoints > 0) || (navigator.msMaxTouchPoints > 0);

        if (isTouchDevice) {
            setIsVisible(false);
            return;
        }

        setIsVisible(true);

        const updatePosition = (e) => {
            setPosition({ x: e.clientX, y: e.clientY });
        };

        const handleMouseDown = () => setIsClicking(true);
        const handleMouseUp = () => setIsClicking(false);

        const handleCursorStyle = () => {
            const target = document.elementFromPoint(position.x, position.y);
            setIsPointer(window.getComputedStyle(target).cursor === 'pointer');
        };

        document.addEventListener('mousemove', updatePosition);
        document.addEventListener('mousemove', handleCursorStyle);
        document.addEventListener('mousedown', handleMouseDown);
        document.addEventListener('mouseup', handleMouseUp);

        return () => {
            document.removeEventListener('mousemove', updatePosition);
            document.removeEventListener('mousemove', handleCursorStyle);
            document.removeEventListener('mousedown', handleMouseDown);
            document.removeEventListener('mouseup', handleMouseUp);
        };
    }, [position.x, position.y]);

    if (!isVisible) return null;

    return (
        <>
            <div
                className={`cursor-dot ${isClicking ? 'clicking' : ''} ${isPointer ? 'pointer' : ''}`}
                style={{ left: `${position.x}px`, top: `${position.y}px` }}
            />
            <div
                className={`cursor-outline ${isClicking ? 'clicking' : ''} ${isPointer ? 'pointer' : ''}`}
                style={{ left: `${position.x}px`, top: `${position.y}px` }}
            />
        </>
    );
};

export default CustomCursor;
