import React from 'react';
import { useNode } from '@craftjs/core';

export const Button = ({ text }) => {
  const { connectors: { connect, drag } } = useNode();
  return (
    <button ref={ref => connect(drag(ref))} style={{ margin: '5px', padding: '10px' }}>
      {text}
    </button>
  );
};
