import React, { useEffect, useState } from 'react';
import './SchoolInfo.css';

const SchoolInfo = () => {
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        setVisible(true);
    }, []);

    return (
        <div className={`school-info ${visible ? 'visible' : ''}`}>
            <h2>JISC Grade-IX</h2>
            <p>We are a proud institution located in Barishal. Our goal is to provide quality education for all students.</p>
            <p>Location: 999 College Row, Barishal 8200</p>
        </div>
    );
};

export default SchoolInfo;
