import React, { useState } from 'react';
import YouTube from 'react-youtube';
import './Navbar.css'; // Import the CSS file
import logo from './logo.png'; // Import the logo
import { Link } from 'react-router-dom'; // Assuming you are using react-router for routing

const Navbar = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const videoOptions = {
        playerVars: {
            autoplay: 1,
            mute: 1,
            loop: 1,
            playlist: 'PUugjmvgWCI', // Example video ID for looping
            start: 0,
            end: 70, // 1:14 mark in seconds
        },
    };

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <div className="navbar">
            <div className="video-background">
                <YouTube
                    videoId="PUugjmvgWCI" // Replace with your YouTube video ID
                    opts={videoOptions}
                    className="youtube-iframe"
                />
            </div>
            <img src={logo} alt="Logo" className="logo" />
            <div className="hamburger-menu" onClick={toggleMenu}>
                <div className="line"></div>
                <div className="line"></div>
                <div className="line"></div>
            </div>
            {menuOpen && (
                <div className="dropdown-menu">
                    <Link to="/" className="menu-item">Home</Link>
                    <Link to="/gallery" className="menu-item">Gallery</Link>
                    <Link to="/chat" className="menu-item">Chatroom</Link>
                </div>
            )}
        </div>
    );
};

export default Navbar;
